import "next-auth";
import "next-auth/jwt";

declare module "next-auth" {
  interface User {
    id: string;
    onboardingComplete?: boolean;
    currency?: string;
    country?: string;
    plan?: string;
  }

  interface Session {
    user: {
      id: string;
      name?: string | null;
      email?: string | null;
      image?: string | null;
      onboardingComplete?: boolean;
      currency?: string;
      country?: string;
      plan?: string;
    };
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id: string;
    onboardingComplete?: boolean;
    currency?: string;
    country?: string;
    plan?: string;
  }
}
